import React, { useState, useEffect } from 'react';
import { fetchRooms, createRoom, deleteRoom, modifyName, modifyBlinds, modifyLuminosity } from '../../data/dataManagementLayer';
import { Link } from 'react-router-dom';
import Header from '../header/header';
import StoreControl from '../storeControl/storeControl';

import './dashboard.css';

const Dashboard = () => {
  const [rooms, setRooms] = useState([]);
  const [newRoomName, setNewRoomName] = useState('');
  const [editRoomName, setEditRoomName] = useState('');
  const [roomBeingEdited, setRoomBeingEdited] = useState(null);
  const [editLuminosityMin, setEditLuminosityMin] = useState(0);
  const [editLuminosityMax, setEditLuminosityMax] = useState(1000);
  const [editAutomatique, setEditAutomatique] = useState('');
  const [error, setError] = useState(null);
  const [blinds, setBlinds] = useState(() => {
    // Get the initial value from localStorage if it exists, otherwise use 100
    const savedBlinds = localStorage.getItem('blinds');
    return savedBlinds !== null ? Number(savedBlinds) : 0;
  });

  useEffect(() => {
    localStorage.setItem('blinds', blinds);
  }, [blinds]);

  useEffect(() => {
    const getRooms = async () => {
      try {
        let roomsFromServer = await fetchRooms();
        if (typeof roomsFromServer === 'object' && !Array.isArray(roomsFromServer)) {
          roomsFromServer = Object.values(roomsFromServer);
        }
        if (Array.isArray(roomsFromServer)) {
          setRooms(roomsFromServer);
        } else {
          console.log('Response from fetchRooms:', roomsFromServer);
        }
      } catch (error) {
        console.error('Error fetching rooms:', error);
        setError(error.toString());
      }
    };

    getRooms();
  }, []);

  const handleAddRoom = async (event) => {
    event.preventDefault();
    const newRoom = { name: newRoomName };
    try {
      const roomFromServer = await createRoom(newRoom.name);
      setRooms((prevRooms) => [...prevRooms, roomFromServer]);
      setNewRoomName('');
      window.location.reload(); // Refresh the page to update the rooms
    } catch (error) {
      console.error('Error creating room:', error);
      setError(error.toString());
    }
  };

  const handleBlindsChange = async (roomId, newBlinds) => {
    console.log(`Attempting to change blinds for room ${roomId} with value ${newBlinds}`);
    try {
      const response = await modifyBlinds(roomId, newBlinds);
      console.log('Response from modifyBlinds:', response);
      setRooms(prevRooms => prevRooms.map(room => {
        if (room.id === roomId) {
          return { ...room, blinds: newBlinds };
        }
        return room;
      }));
      window.location.reload();
    } catch (error) {
      console.error('Error modifying blinds:', error);
      setError(error.toString());
    }
  };

  const handleModification = async (roomId, newName, luminositeMin, luminositeMax, automatique) => {
    try {
      const automatiqueBool = automatique === 'true' ? true : false;
      await modifyName(roomId, newName, luminositeMin, luminositeMax, automatiqueBool);
      setRooms(prevRooms => prevRooms.map(room => {
        if (room.id === roomId) {
          return { ...room, name: newName, luminositeMin: editLuminosityMin, luminositeMax: editLuminosityMax, automatique: automatiqueBool };
        }
        return room;
      }));
      setRoomBeingEdited(null); // Reset the room being edited
      window.location.reload(); // Refresh the page to update the rooms
    } catch (error) {
      console.error('Error modifying room :', error);
      setError(error.toString());
    }
  };

  const handleRemoveRoom = async (roomId) => {
    try {
      await deleteRoom(roomId);
      setRooms(rooms.filter(room => room.id !== roomId));
    } catch (error) {
      console.error('Error deleting room:', error);
      setError(error.toString());
    }
  };

  const handleLuminosityChange = (roomName, period) => {
    const luminosityValues = {
      night: { min: 0, max: 250 },
      morning: { min: 250, max: 500 },
      day: { min: 500, max: 750 },
      evening: { min: 750, max: 1000 }
    };

    const { min, max } = luminosityValues[period];

    setRooms((prevRooms) => prevRooms.map((room) => {
      if (room.name === roomName) {
        return { ...room, luminositeMin: min, luminositeMax: max };
      }
      return room;
    }));

    modifyLuminosity(roomName, min, max); // Call the API to modify the luminosity
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="dashboard-container">
      <header className="header">
        <Header />
      </header>
      <div className="dashboard">
        <h1 className="dashboard-title">Dashboard</h1>
        <div className="rooms">
          {rooms.length > 0 ? rooms.map((room, index) => (
            <div className={`room room-${index}`} key={index}>
              <div className="room-header">
                <h2 className="room-name">Room : {room.name}</h2>
                <Link to="/simulation">
                  <img 
                    src="https://github.com/TenkaGhoul/IoT_Project_Unicorn_2024/blob/frontend-paul/src/info.png?raw=true" 
                    alt="Go to simulation" 
                    className="simulation-link" 
                    title="If you want to see how bright you want your room to be."
                  />
                </Link>
              </div>
              <p className="room-id">ID: {room.id}</p>
              <hr className="room-divider" />
              {room.automatique && (
                <>
                  <p className="room-luminosity-max">Maximum Luminosity: {room.luminositeMax}</p>
                  <p className="room-luminosity-min">Minimum Luminosity: {room.luminositeMin}</p>
                  <p className="room-blinds">Blinders opening: {room.blinds} %</p>
                  <p className="room-automatic">Automatic configuration: {room.automatique ? 'True' : 'False'}</p>
                </>
              )}
              {room.automatique === false && (
                  <>
                    <StoreControl room={room} onChange={(value) => {
                        setBlinds(value);
                    }}/>
                    <div className="blinds-control">
                        <p className="room-blinds">Blinders opening: {room.blinds} %</p>
                        <button id="btn-save" onClick={() => handleBlindsChange(room.name, blinds)}>Save</button>
                    </div>
                    <p className="room-automatic">Automatic configuration: {room.automatique ? 'True' : 'False'}</p>
                  </>
              )}
              <hr className="room-divider" />
              <div className="room-actions">
                <button className="room-delete-button" onClick={() => handleRemoveRoom(room.id)}>Delete</button>
                <button className="room-edit-button" onClick={() => {
                  setRoomBeingEdited(room.id);
                  setEditRoomName(room.name);
                  setEditLuminosityMin(room.luminositeMin);
                  setEditLuminosityMax(room.luminositeMax);
                  setEditAutomatique(room.automatique.toString());
                }}>Edit</button>            
                {roomBeingEdited === room.id && (
                  <form onSubmit={(e) => {
                    e.preventDefault();
                    handleModification(room.id, editRoomName, editLuminosityMin, editLuminosityMax, editAutomatique === 'true' ? 'true' : 'false');
                  }}>
                    <input
                    className="room-name-input"
                      type="text"
                      value={editRoomName}
                      onChange={(e) => setEditRoomName(e.target.value)}
                      placeholder="Enter new room name"
                    />
                    <select
                      className="room-automatique-input"
                      value={editAutomatique}
                      onChange={(e) => setEditAutomatique(e.target.value)}
                    >
                      <option value="true">True</option>
                      <option value="false">False</option>
                    </select>
                      {room.automatique && (
                      <>
                        <input
                          className="room-luminosity-input"
                          type="number"
                          value={editLuminosityMin}
                          onChange={(e) => setEditLuminosityMin(e.target.value)}
                          placeholder="Min luminosity"
                        />
                        <input
                          className="room-luminosity-input"
                          type="number"
                          value={editLuminosityMax}
                          onChange={(e) => setEditLuminosityMax(e.target.value)}
                          placeholder="Max luminosity"
                        />
                      </>
                    )}
                    <button type="submit" className="room-edit-button">Save</button>
                  </form>
                )}
                <div className="room-preset"></div>
                  <button id="btn-preset" onClick={() => handleBlindsChange(room.name, 100)}>Open</button>
                  <button id="btn-preset" onClick={() => handleBlindsChange(room.name, 0)}>Close</button>
                  <button id="btn-preset" onClick={() => handleBlindsChange(room.name, 50)}>Mid-opening</button>
                </div>
            {room.automatique && (
              <div>
                <hr className="room-divider" />
                <p>
                  These are interactive presets, you can click on them to change the minimum and maximum luminosity of the room.
                </p>
              </div>
            )}
            { room.automatique && (
              <div className="luminosity-buttons">
                <button id="btn" onClick={() => handleLuminosityChange(room.name, 'night')}>
                  <img id="img" src="data:image/png;base64," alt="Night icon" title="Night preset" />
                </button>
                <button id="btn" onClick={() => handleLuminosityChange(room.name, 'morning')}>
                  <img id="img" src="data:image/png;base64," alt="Morning icon" title="Morning preset" />
                </button>
                <button id="btn" onClick={() => handleLuminosityChange(room.name, 'day')}>
                  <img id="img" src="data:image/png;base64," alt="Day icon" title="Day preset" />
                </button>
                <button id="btn-evening"onClick={() => handleLuminosityChange(room.name, 'evening')}>
                  <img id="img-evening" src="data:image/png;base64," alt="Evening icon" title="Evening preset" />
                </button>
              </div>
              )}
          </div>
        )) : <p>No rooms available</p>}
        </div>
        <hr className="dashboard-divider"/>
        <form className="room-add-form" onSubmit={handleAddRoom}>
          <input
            type="text"
            value={newRoomName}
            onChange={(e) => setNewRoomName(e.target.value)}
            placeholder="Enter room name"
            required
            className="room-name-input"
          />
          <button type="submit" className="room-add-button">Add Room</button>
        </form>
        </div>
      {error && <div className="error-message">Error: {error}</div>}
    </div>
  );
}

export default Dashboard;